import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import { useEffect,useState } from "react";
const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];
const AvailableMeals = () => {
  const [meals,setMeals]=useState([]);
useEffect(()=>{
  const fetchMeals=async()=>{
  const response=await fetch('https://fooddelivery-7d253-default-rtdb.firebaseio.com/meals.json');
  const respnseData=await response.json();

  const loadedMeals=[];
  for(const key in respnseData){
    loadedMeals.push({
      id:key,
      name:respnseData[key].name,
      description:respnseData[key].description,
      price:respnseData[key].price
    })
  }
  setMeals(loadedMeals)
  }
fetchMeals();
},[])

  const mealsList =meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};
export default AvailableMeals;
